import { useState } from "react";

export default function Botao() {
  const [count, setCount] = useState(0);

  return (
    <>
    <p></p>
      <button onClick={(event) => setCount(count + 1)}>
        Clicked {count} times
      </button>
      <button onClick={(event) => setCount(count - 1)}>click {count}times </button>
    </>
  );
}
