import { useState } from "react";

export default function Botao() {
  const [count, setCount, setconta, conta] = useState(0);

  return (
    <>
      <button onClick={(event) => setCount(count + 1)}>
        Clicked {count} times
      </button>
      <button onClick={(event) => setconta(conta - 1)}>click {conta}times </button>
    </>
  );
}
